import React from 'react';
import classes from './Cart.module.css';

const Checkout = (props) => {
    const [name, setName] = React.useState('');
    const [street, setStreet] = React.useState('');
    const [postal, setPostal] = React.useState(0);
    const [city, setCity] = React.useState('');
    const [hasError, setHasError] = React.useState({
        name: false,
        street: false,
        postal: false,
        city: false,
    });

    const nameInputHandler = (e) => {
        setName(e.target.value);
        if (name.length <= 4) {
            setHasError({ ...hasError, name: true });
        }
        setHasError({ ...hasError, name: false });
    };
    const streetInputHandler = (e) => {
        setStreet(e.target.value);
    };
    const postalInputHandler = (e) => {
        setPostal(e.target.value);
    };
    const cityInputHandler = (e) => {
        setCity(e.target.value);
    };

    const submitionHandler = (e) => {
        e.preventDefault();
        if (street.length <= 5) {
            setHasError({ ...hasError, name: false, street: true });
            return null;
        } else if (postal <= 1000) {
            setHasError({
                ...hasError,
                street: false,
                name: false,
                postal: true,
            });
            return null;
        } else if (city.length <= 1) {
            setHasError({
                postal: false,
                street: false,
                name: false,
                city: true,
            });
            return null;
        } else {
            setHasError({
                postal: false,
                street: false,
                name: false,
                city: false,
            });
            props.onConfirm({
                name: name,
                street: street,
                postal: postal,
                city: city,
            });
        }
    };

    return (
        <form onSubmit={submitionHandler}>
            <div>
                <label>Name</label>
                <input
                    onChange={nameInputHandler}
                    value={name}
                    type='text'
                ></input>
                {hasError.name && <p className='error'>Something is wrong</p>}
            </div>
            <div>
                <label>Street</label>
                <input
                    onChange={streetInputHandler}
                    value={street}
                    type='text'
                ></input>
                {hasError.street && <p className='error'>Something is wrong</p>}
            </div>
            <div>
                <label>Postal Code</label>
                <input
                    onChange={postalInputHandler}
                    value={postal}
                    type='number'
                ></input>
                {hasError.postal && <p className='error'>Something is wrong</p>}
            </div>
            <div>
                <label>City</label>
                <input
                    onChange={cityInputHandler}
                    value={city}
                    type='text'
                ></input>
                {hasError.city && <p className='error'>Something is wrong</p>}
            </div>
            <div className={classes.actions}>
                <button onClick={props.onCancel} className={classes.button}>
                    Cancel
                </button>
                <button type='submit' className={classes.button}>
                    Confirm
                </button>
            </div>
        </form>
    );
};

export default Checkout;
