import React from 'react';
import classes from './Cart.module.css';

const Checkout = (props) => {
    const [name, setName] = React.useState('');
    const [street, setStreet] = React.useState('');
    const [postal, setPostal] = React.useState(0);
    const [city, setCity] = React.useState('');
    const [hasError, setHasError] = React.useState(false);

    const nameInputHandler = (e) => {
        setName(e.target.value);
    };
    const streetInputHandler = (e) => {
        setStreet(e.target.value);
    };
    const postalInputHandler = (e) => {
        setPostal(e.target.value);
    };
    const cityInputHandler = (e) => {
        setCity(e.target.value);
    };

    const submitionHandler = (e) => {
        e.preventDefault();
        if (name.length > 4) {
            if(street.length > 5 ){
                if(postal >= 1000 ){
                    if(city.length > 1){

                    }
                }
            }
            setHasError(true);
        }else{
            props.onConfirm({
                name: name,
                street: street,
                postal: postal,
                city: city,
            });
        }
    };

    return (
        <form onSubmit={submitionHandler}>
            <div>
                <label>Name</label>
                <input
                    onChange={nameInputHandler}
                    value={name}
                    type='text'
                ></input>
            </div>
            <div>
                <label>Street</label>
                <input
                    onChange={streetInputHandler}
                    value={street}
                    type='text'
                ></input>
            </div>
            <div>
                <label>Postal Code</label>
                <input
                    onChange={postalInputHandler}
                    value={postal}
                    type='number'
                ></input>
            </div>
            <div>
                <label>City</label>
                <input
                    onChange={cityInputHandler}
                    value={city}
                    type='text'
                ></input>
            </div>
            <div className={classes.actions}>
                <button onClick={props.onCancel} className={classes.button}>
                    Cancel
                </button>
                <button type='submit' className={classes.button}>
                    Confirm
                </button>
            </div>
            {hasError && <p className='error'>Something is wrong</p>}
        </form>
    );
};

export default Checkout;
