import React from 'react';
import Modal from '../components/UI/Modal';
import classes from '../components/UI/Modal.module.css';
import { useState } from 'react';

const OrdersCart = (props) => {
    const [myOrders, setMyOrders] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    async function fetchOrdersHandler() {
        setIsLoading(true);
        try {
            const response = await fetch(
                'https://react-http-467cc-default-rtdb.firebaseio.com/orders.json'
            );
            if (response.ok) {
                console.log('All right!');
            }
            const data = await response.json();
            const loadedOrders = [];
            for (const key in data) {
                console.log(key);
                key.forEach((e) => {
                    loadedOrders.push({
                        id: e.id,
                        amount: e.amount,
                        price: e.price,
                        amount: e.amount,
                    });
                });
            }
            setMyOrders(loadedOrders);
        } catch (e) {
            console.log(e.mesage);
        }
        setIsLoading(false);
    }
    const totalAmount = () => {
        let total = 0;
        for (const a of myOrders) {
            total = total + a.price * a.amount;
        }
        return total;
    };

    return (
        <Modal onClose={props.onClose}>
            {isLoading ? (
                <p>Loading...</p>
            ) : (
                <button onClick={fetchOrdersHandler}>Fetch orders</button>
            )}
            {myOrders.map((e) => {})}
            <div className={classes.total}>
                <span>Total Amount</span>
                <span>{totalAmount}</span>
            </div>
            <div className={classes.actions}>
                <button className={classes['button--alt']}>Close</button>
            </div>
        </Modal>
    );
};

export default OrdersCart;
