import React from 'react';
import Modal from '../components/UI/Modal';
import classes from '../components/Cart/Cart.module.css';
import { useState } from 'react';
import CartIcon from '../components/Cart/CartIcon';

const OrdersCart = (props) => {
    const [myOrders, setMyOrders] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [name, setName] = useState('');
    async function fetchOrdersHandler() {
        setIsLoading(true);
        try {
            const response = await fetch(
                'https://react-http-467cc-default-rtdb.firebaseio.com/orders.json'
            );
            if (response.ok) {
                console.log('All right!');
            }
            const data = await response.json();

            const loadedOrders = [];
            let found = false;

            for (const key in data) {
                if (data[key].user.name === name && !found) {
                    found = true;
                }
                if (found) {
                    for (const item of data[key].order) {
                        console.log(item); //3
                        loadedOrders.push({
                            ...item,
                        });
                    }
                    setMyOrders(loadedOrders);
                }
            }

        } catch (e) {
            console.log(e.message);
        }
        setIsLoading(false);
    }

    const nameHandler = (e) => {
        setName(e.target.value);
    };

    let total = 0;
    for (const a of myOrders) {
        total = total + a.price * a.amount;
    }
    const totalAmount = total;

    return (
        <Modal onClose={props.onClose}>
            {isLoading ? (
                <p>Loading...</p>
            ) : (
                <div>
                    <label>Name</label>
                    <input
                        className={classes.inputfetch}
                        onChange={nameHandler}
                        value={name}
                    ></input>
                    {name.length > 3 && (
                        <button
                            className={classes.buttonf}
                            onClick={fetchOrdersHandler}
                        >
                            Fetch orders
                        </button>
                    )}
                </div>
                
            )}
            {myOrders.map((e) => {
                return <CartItem
                    key={e.key}
                    name={e.name}
                    amount={e.amount}
                    price={e.price}
                    //onRemove={cartItemRemoveHandler.bind(null, item.id)}
                    //onAdd={cartItemAddHandler.bind(null, item)}
                />;
            })}
            <div className={classes.total}>
                <span>Total Amount</span>
                <span>{totalAmount}</span>
            </div>
            <div className={classes.actions}>
                <button onClick={props.onClose} className={classes.button}>
                    Close
                </button>
            </div>
        </Modal>
    );
};

export default OrdersCart;
