import React from 'react';
import Modal from '../components/UI/Modal';
import classes from '../components/Cart/Cart.module.css';
import { useState } from 'react';
import CartIcon from '../components/Cart/CartIcon';

const OrdersCart = (props) => {
    const [myOrders, setMyOrders] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    async function fetchOrdersHandler() {
        setIsLoading(true);
        try {
            const response = await fetch(
                'https://react-http-467cc-default-rtdb.firebaseio.com/orders.json'
            );
            if (response.ok) {
                console.log('All right!');
            }
            const data = await response.json();
            const loadedOrders = [];
            console.log(data[0]);
            setMyOrders(loadedOrders);
        } catch (e) {
            console.log(e.message);
        }
        setIsLoading(false);
    }
    let total = 0;
    for (const a of myOrders) {
        total = total + a.price * a.amount;
    }
    const totalAmount = total;

    return (
        <Modal onClose={props.onClose}>
            {isLoading ? (
                <p>Loading...</p>
            ) : (
                <button className={classes.button} onClick={fetchOrdersHandler}>Fetch orders</button>
            )}
            {myOrders.map((e) => {
                <CartIcon
                    key={e.id}
                    name={item.name}
                    amount={item.amount}
                    price={item.price}
                    //onRemove={cartItemRemoveHandler.bind(null, item.id)}
                    //onAdd={cartItemAddHandler.bind(null, item)}
                />;
            })}
            <div className={classes.total}>
                <span>Total Amount</span>
                <span>{totalAmount}</span>
            </div>
            <div className={classes.actions}>
                <button className={classes.button}>Close</button>
            </div>
        </Modal>
    );
};

export default OrdersCart;
