import React from 'react';
import { useState } from 'react';

const OrdersCart = () => {
    const [myOrders, setMyOrders] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    async function fetchOrdersHandler() {
        setIsLoading(true);
        const response = await fetch(
            'https://react-http-467cc-default-rtdb.firebaseio.com/orders.json'
        );
        if (response.ok) {
            console.log('All right!');
        }
        const data = await response.json();
        const loadedOrders = [];
        for (const key in data) {
            console.log(key);
            key.forEach((e) => {
                loadedOrders.push({
                    id: e.id,
                    amount: e.amount,
                    price: e.price,
                    amount: e.amount,
                });
            });
        }
        setMyOrders(loadedOrders);
        setIsLoading(false);
    }
    const totalAmount = ()=>{
        let total =0;
        for(const a of myOrders){
            total = total +(a.price * a.amount)
        }
    }

    
    fetchOrdersHandler();

    return (
        <React.Fragment>
            <div>
                
                {isLoading ? (
                    <p>Loading...</p>
                ) : (
                    <button onClick={fetchOrdersHandler}>Fetch orders</button>
                )}
            </div>
            <Modal onClose={props.onClose}>
            {loadedOrders.map((e)=>{})}
            <div className={classes.total}>
                <span>Total Amount</span>
                <span>{totalAmount}</span>
            </div>
            <div className={classes.actions}>
                <button
                    className={classes['button--alt']}
                    onClick={props.onClose}
                >
                    Close
                </button>
                {hasItems && (
                    <button onClick={postingOrder} className={classes.button}>
                        Order
                    </button>
                )}
            </div>
            {orderWasSent && sent}
        </Modal>
        </React.Fragment>
    );
};

export default OrdersCart;
