            </div>
            <div>
                <label>Street</label>
                <input onChange={streetInputHandler} value={street} type='text'></input>
            </div>
            <div>
                <label>Postal Code</label>
                <input onChange={postalInputHandler} value={postal} type='number'></input>
            </div>
